import React from "react";
import { proy } from "./constantes";
import { menu } from "./constantes";
import { tar } from "./constantes";
import { usr } from "./constantes";

export function menuproyectos(e) {
    if (e && typeof e.preventDefault === 'function') e.preventDefault();
    const container = document.getElementById('bopc');
    if (!container) return;
    container.innerHTML = proy
        .map(
            (element) => `
            <ul>
                <li><a href="#mospro">Mostrar</a></li>
                <li><a href="#crepro">Crear</a></li>
                <li><a href="#updpro">Actualizar</a></li>
                <li><a href="#elimpro">Eliminar</a></li>
                <li><a href="#regpro">Regresar</a></li>
            </ul> 
        `
        )
        .join('');
}
export function menutar(e) {
    if (e && typeof e.preventDefault === 'function') e.preventDefault();
    const container = document.getElementById('bopc');
    if (!container) return;
    container.innerHTML = tar
        .map(
            (element) => `
            <ul>
                <li><a href="#mostar">Mostrar</a></li>
                <li><a href="#cretar">Crear</a></li>
                <li><a href="#updtar">Actualizar</a></li>
                <li><a href="#elimtar">Eliminar</a></li>
                <li><a href="#regtar">Regresar</a></li>
            </ul> 
        `
        )
        .join('');
}
export function menuusuarios(e) {
    if (e && typeof e.preventDefault === 'function') e.preventDefault();
    const container = document.getElementById('bopc');
    if (!container) return;
    container.innerHTML = usr
        .map(
