                <li><a href="#updusr">Actualizar</a></li>
                <li><a href="#elimusr">Eliminar</a></li>
                <li><a href="#regusr">Regresar</a></li>
            </ul> 
        `
        )
        .join('');
}
export function menuregresar(e) {
    if (e && typeof e.preventDefault === 'function') e.preventDefault();
    const container = document.getElementById('bopc');
    if (!container) return;
    container.innerHTML = menu
        .map(
            (element) => `
            <ul>
                <li><a href="#" onClick={menuproyectos}>Proyectos</a></li>
                <li><a href="#tareas">Tareas</a></li>
                <li><a href="#usuarios">Usuarios</a></li>
            </ul> 
        `
        )
        .join('');
}
