import React, { useState } from 'react';
import axios from 'axios';

export default function CrearProyecto({ onCreated }) {
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [cliente, setCliente] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!nombre.trim()) {
      setMessage('El nombre es requerido.');
      return;
    }
    setLoading(true);
    setMessage('');
    try {
      const payload = { nombre: nombre.trim(), descripcion: descripcion.trim(), cliente: cliente.trim() };
      await axios.post('http://localhost:3001/proyectos', payload);
      setMessage('Proyecto creado correctamente.');
      setNombre('');
      setDescripcion('');
      setCliente('');
      if (typeof onCreated === 'function') onCreated();
    } catch (err) {
      // eslint-disable-next-line no-console
      console.error('Error al crear proyecto:', err);
      setMessage('Error al crear proyecto.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Crear Proyecto</h2>
      <form onSubmit={handleSubmit} id="form-crepro-react">
        <div className="form-row">
          <label htmlFor="crepro-nombre">Nombre</label>
          <input id="crepro-nombre" name="nombre" type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} />
        </div>
        <div className="form-row">
          <label htmlFor="crepro-descripcion">Descripci√≥n</label>
          <input id="crepro-descripcion" name="descripcion" type="text" value={descripcion} onChange={(e) => setDescripcion(e.target.value)} />
        </div>
        <div className="form-row">
          <label htmlFor="crepro-cliente">Cliente</label>
          <input id="crepro-cliente" name="cliente" type="text" value={cliente} onChange={(e) => setCliente(e.target.value)} />
        </div>
        <div className="form-row">
          <button id="crepro-guardar" type="submit" disabled={loading}>{loading ? 'Guardando...' : 'Guardar'}</button>
        </div>
      </form>
      {message && <div id="crepro-mensaje" style={{ marginTop: 10 }}>{message}</div>}
    </div>
  );
}
