import React from "react";
import { proy } from "./constantes";
import { tar } from "./constantes";
import { usr } from "./constantes";

export function menuproyectos(e) {
    if (e && typeof e.preventDefault === 'function') e.preventDefault();
    const container = document.getElementById('mensec');
    if (!container) return;
    container.innerHTML = proy
        .map(
            (element) => `
            <ul>
                <li><a href="#mospro">Mostrar</a></li>
                <li><a href="#crepro">Crear</a></li>
                <li><a href="#updpro">Actualizar</a></li>
                <li><a href="#elimpro">Eliminar</a></li>
            </ul> 
        `
        )
        .join('');
}
export function menutar(e) {
    if (e && typeof e.preventDefault === 'function') e.preventDefault();
    const container = document.getElementById('mensec');
    if (!container) return;
    container.innerHTML = tar
        .map(
            (element) => `
            <ul>
                <li><a href="#mostar">Mostrar</a></li>
                <li><a href="#cretar">Crear</a></li>
                <li><a href="#updtar">Actualizar</a></li>
                <li><a href="#elimtar">Eliminar</a></li>
            </ul> 
        `
        )
        .join('');
}
export function menuusuarios(e) {
    if (e && typeof e.preventDefault === 'function') e.preventDefault();
    const container = document.getElementById('mensec');
    if (!container) return;
    container.innerHTML = usr
        .map(
            (element) => `
            <ul>
                <li><a href="#mosusr">Mostrar</a></li>
                <li><a href="#creusr">Crear</a></li>
                <li><a href="#updusr">Actualizar</a></li>
                <li><a href="#elimusr">Eliminar</a></li>
            </ul> 
        `
        )
        .join('');
}
export const gabinete = [
    { id: 1, nombre: "Radiografia", precio: 150 },
    { id: 2, nombre: "Tomografia", precio: 200 },
    { id: 3, nombre: "Resonancia", precio: 250 },
    { id: 4, nombre: "Mamografia", precio: 180 },
    { id: 5, nombre: "Ecografia", precio: 160 },
    { id: 6, nombre: "Endoscopia", precio: 200 }
];

export function mostrarGabinete(e) {
    if (e && typeof e.preventDefault === 'function') e.preventDefault();
    const container = document.getElementById('muestra');
    if (!container) return;
    container.innerHTML = gabinete
        .map(
            (s) => `
                <div class="servicio">
                    <h2>${s.nombre}</h2>
                    <p>Precio: $${s.precio}</p>
                </div>`
        )
        .join('');
}
