import React from "react";
import { proy } from "./constantes";

export function menuproyectos(e) {
    if (e && typeof e.preventDefault === 'function') e.preventDefault();
    const container = document.getElementById('mens');
    if (!container) return;
    container.innerHTML = proy
        .map(
            (item) =>
                item.props.children
                    .map(
                        (li) =>
                            `<li><a href="${li.props.children.props.href}">${li.props.children.props.children}</a></li>`
                    )
                    .join('')
        )
        .join('');
}
export function mospro(e) {
    if (e && typeof e.preventDefault === 'function') e.preventDefault();
    const container = document.getElementById('contenido');
    if (!container) return;
    container.innerHTML = '<h2>Mostrar Proyectos</h2><p>Aquí se mostrarán los proyectos.</p>';
}
